mapper.select:
SELECT

FROM lmmall_cart
WHERE user_id=#{userId}
AND product_id=#{productId}

SELECT
COUNT(1)
FROM lmmall_cart
WHERE user_id = #{userId}
AND checked != 1;

mapper.delete:
DELETE
FROM lmmall_cart
WHERE user_id = #{userId}

  AND product_id IN

    #{item}

mapper.update:
UPDATE lmmall_cart
SET checked=#{checked},
update_time = now()
WHERE user_id=#{userId}

  AND product_id = #{productId}
