mapper.select:
SELECT

FROM lmmall_user
WHERE username=#{username}
AND password=#{password}

SELECT
COUNT(1)
FROM lmmall_user
WHERE username=#{username}
AND question=#{question}
AND answer=#{answer}

mapper.update:
UPDATE lmmall_user
SET password=#{password},update_time=now()
WHERE username=#{username}

mapper.select:
SELECT
COUNT(1)
FROM lmmall_user
WHERE email=#{email}
AND id=#{id}

SELECT
COUNT(1)
FROM lmmall_user
WHERE phone=#{phone}
AND id=#{id}
